mapper.select:
select

from t_sys_opt_log t


		and t.id =  #{id}


		and t.trace_id =  #{traceId}


		and t.module =  #{module}


		and t.title =  #{title}


		and t.method =  #{method}


		and t.url =  #{url}


		and t.headers =  #{headers}


		and t.request_body =  #{requestBody}


		and t.response_body =  #{responseBody}


		and t.request_ip =  #{requestIp}


		and t.exception =  #{exception}


		and t.execute_time =  #{executeTime}


		and t.tenant_id = #{tenantId}


		and t.create_by =  #{createBy}


		and t.create_time >= #{searchStartTime}


		and t.create_time <=  #{searchEndTime}


order by t.id desc

		select

 		from t_sys_opt_log t


 				and t.id =  #{id}


 				and t.trace_id =  #{traceId}


 				and t.module =  #{module}


 				and t.title =  #{title}


